export type EventCategory = 'science' | 'technology' | 'publication' | 'institution' | 'digital' | 'community'

export type ReadingLevel = 'simple' | 'standard' | 'detailed'
export type Density = 'condensed' | 'standard' | 'full'

export interface TimelineEvent {
  year: number
  endYear?: number
  approximate?: boolean
  title: string
  description: string
  /** Kid-friendly, jargon-free version */
  descriptionSimple?: string
  /** Scholarly, adds technical depth */
  descriptionDetailed?: string
  category: EventCategory
  people?: string[]
  links?: { label: string; url: string }[]
  /** Optional image â€” path relative to public/, with alt text and optional credit */
  image?: { src: string; alt: string; credit?: string }
  significance: 1 | 2 | 3
  inflectionPoint?: boolean
}

/** Get the right description for the chosen reading level */
export function getDescription(event: TimelineEvent, level: ReadingLevel): string {
  if (level === 'simple') return event.descriptionSimple ?? event.description
  if (level === 'detailed') return event.descriptionDetailed ?? event.description
  return event.description
}

/** Filter events by density setting */
export function filterByDensity(events: TimelineEvent[], density: Density): TimelineEvent[] {
  if (density === 'condensed') return events.filter(e => e.significance === 1)
  if (density === 'standard') return events.filter(e => e.significance <= 2)
  return events // 'full' = everything
}

export interface Era {
  label: string
  start: number
  end: number
  color: string
}

export interface ThematicThread {
  theme: string
  description: string
  keyMoments: string[]
}

